import React, {useState} from "react";
import "./Login.css"



const Login = ()=>{
    const [log, setLog]= useState("");
    const [pass, setPass] = useState("")
    const [drawUser, setDrawUser] = useState("")

    const handlLog = (event)=>{
        setLog(event.target.value)
    }

    const hadlPass = (event)=>{
        setPass(event.target.value)
    }

    const user = {
        user1:[
            {login:"rus",
            password:"123"}
        ],
        user2:[
            {login:"nurlan",
            password:"321"}
        ]
    }
   

    const singButton =()=>{
        console.log(user[0])

        for (let i = 0; i < user.length; i++){

            console.log(user[i].login[0])


            if(log === user[i].login && pass === user[i].password){
                console.log("wellcom")
                const draw = <div> wellcom </div>
                setDrawUser(draw)
               }else{
                console.log("error Log or Pass")
                const draw = <div> error Log or Pass </div>
                setDrawUser(draw)
               }
        
        }


    }


    return(
        <div className="container">
            <div className="box">
            <input className="input" placeholder="Login" value = {log} onChange = {handlLog}></input>
            <input className="input" placeholder="Password" value = {pass} onChange={hadlPass}></input>
            <button className="button" onClick={singButton}>Sign Up</button>

            <div>
                {drawUser}
            </div>
            </div>
        </div>
        
    )
}

export default Login
