import React, {useState,useEffect} from "react";


const Artist = ()=>{
    
    const [inputSearch, setInputSearch] = useState('');
    const [resultImg, setResultImg] = useState('');
    
    
    
   
    const getArtist = async()=>{
        const options = {
            method: 'GET',
            headers: {
                'X-RapidAPI-Key': 'f1d641037bmsh805777c136ad783p142fecjsn73217d65f347',
                'X-RapidAPI-Host': 'genius.p.rapidapi.com'
            }
        };
        
        const response = await fetch(`https://genius.p.rapidapi.com/search?q=${inputSearch}`, options)
            const data = await response.json()
            console.log(data)

            // const artistSong = data.response.map(song=>
            
            // <div key={song.id}>
            //     <img src={song.hits.result.header_image_thumbnail_url} alt="foto" />
            // </div>)
            // let add = 0
            for(let i =0; i < data.response.hits.length; i++){
                i+=i
                let add = 
                <div key = {data.response.hits}>
                    <img src={data.response.hits[i].result.header_image_thumbnail_url} alt="foto" />
                    <p>{data.response.hits[i].result.full_title}</p>
                </div>
                console.log(add)
                setResultImg(add)
            }
            
            // setResult(artistSong)
            

    }

    useEffect(()=>{
        getArtist(inputSearch )
    }, [])

    const handleChange =(event)=>{
        setInputSearch(event.target.value)
    }

    
    return(
        <div>
            
            <div className="box">
                <div>
                    <input value={inputSearch} onChange={handleChange}></input>
                </div>
                <button className="btn" onClick={getArtist}>Button</button>
                
                
                {resultImg}


            </div>
            
        </div>
            
    )
    
   
}

export default Artist;
