import React, {useState,useEffect} from "react";

const MarsCamera = ()=>{
    
    const [selectName, setSelectName]= useState('')
    const [selectCameras, setSelectCameras]= useState('')

    const [first,setFirst] = useState('')
    const [second, setSecond]= useState('')

    const rovers = [
        {
            name:"curiosity",
            cameras:['fhaz','rhaz','mast','chemcam','mahli','mardi','navcam']
        },
        {
            name:"opportunity",
            cameras:['fhaz','rhaz','navcam','pancam','minites']
        },
        {
            name:"spirit",
            cameras:['fhaz','rhaz','navcam','pancam','minites']
        },
    ];



    useEffect(()=>{
        setSelectName((rovers.map(obj=>(
            <option key = {obj.name} value={obj.name}>{obj.name}</option>
        ))))
    
    },[])
    useEffect(()=>{
        for (let i = 0; i < rovers.length; i++) {
            if (rovers[i].name = selectName) {
                // index = i;
                setSelectCameras(rovers[i].cameras.map(cameraobj=>(
                    <option key={cameraobj} value={cameraobj}>{cameraobj}</option>
                )))
                
            }
        }

    
    },[selectName])
    
    const handleFirst = (event)=>{
        setFirst(event.target.value)
    }

    const handleSecond = (event)=>{
        setSecond(event.target.value)
    }
   
    return(
        <div>
            <select value = {first} onChange={handleFirst}>
                {selectName}
            </select>
            <select value = {second} onChange={handleSecond}>
                {selectCameras}

            </select>
            <input>Введи время в секундах</input>
            <button >Choose</button>
        </div>
    )



}

export default MarsCamera;
