import React, {useState,useEffect} from "react";

const MarsCamera = ()=>{
    
    const [selectName, setSelectName]= useState('')
    const [selectCameras, setSelectCameras]= useState('')

    const [first,setFirst] = useState('')
    const [second, setSecond]= useState('')

    const rovers = [
        {
            name:"curiosity",
            cameras:['fhaz','rhaz','mast','chemcam','mahli','mardi','navcam']
        },
        {
            name:"opportunity",
            cameras:['fhaz','rhaz','navcam','pancam','minites']
        },
        {
            name:"spirit",
            cameras:['fhaz','rhaz','navcam','pancam','minites']
        },
    ];

    // const drawSelect = ()=>{
    //     setSelectName(rovers.name(obj=>(
    //         <option key = {obj} value={obj}>{obj}</option>
    //     )))
    // }

    useEffect(()=>{
        setSelectName((rovers.map(obj=>(
            <option key = {obj.name} value={obj.name}>{obj.name}</option>
        ))))
    
    },[])
    useEffect(()=>{
        // setSelectCameras((rovers.map(obj=>(
        //     <option key = {obj.cameras} value={obj.cameras}>{obj.cameras}</option>
        // ))))
