        //         cameras:['fhaz','rhaz','mast','chemcam','mahli','mardi','navcam']
        //     }


        //     selectCameras ()
        // }

        // const index = rovers.indexOf(selectName.value)
        // console.log(index);
        // let index = 0;
        for (let i = 0; i < rovers.length; i++) {
            if (rovers[i].name = selectName) {
                // index = i;
                setSelectCameras(rovers[i].cameras.map(cameraobj=>(
                    <option key={cameraobj} value={cameraobj}>{cameraobj}</option>
                )))
                
            }
        }




     
    
    },[selectName])
    
   
    return(
        <div>
            <select value = {first} onChange={handleFirst}>
                {selectName}
            </select>
            <select value = {second} onChange={handleSecond}>
                {selectCameras}

            </select>
            <button >Choose</button>
        </div>
    )



}

export default MarsCamera;
