import React, {useState,useEffect} from "react";
import "./MarsCamera.css"

const MarsCamera = ()=>{
    const [selectName, setSelectName]= useState('')   
    const [selectCameras, setSelectCameras]= useState('') 

    

    const [first,setFirst] = useState('')  // 1 select
    const [second, setSecond]= useState('') // 2 select
    const [valInput, setValInput] = useState('') // Input
    const [result, setResult] = useState('')

    const rovers = [
        {
            name:"curiosity",
            cameras:['fhaz','rhaz','mast','chemcam','mahli','mardi','navcam']
        },
        {
            name:"opportunity",
            cameras:['fhaz','rhaz','navcam','pancam','minites']
        },
        {
            name:"spirit",
            cameras:['fhaz','rhaz','navcam','pancam','minites']
        },
    ];

    useEffect(() => {    
        setSelectName(rovers.map(obj=>(
            <option key = {obj.name} value={obj.name}>{obj.name}</option>
        )))
        setFirst(rovers[0].name)
        setSelectCameras(rovers[0].cameras.map(el => (           // Для начального параметр для 2-го селекта
            <option key={el} value={el}>{el}</option>
        )))
        setSecond(rovers[0].cameras[0])
    }, [])

    useEffect(()=>{
        for (let i = 0; i < rovers.length; i++) {
            if (rovers[i].name === first) {
                setSelectCameras(rovers[i].cameras.map(cameraobj=>(
                    <option key={cameraobj} value={cameraobj}>{cameraobj}</option>
                )))
            }
        }
        console.log('first select: ' + first, 'second select: ' + second);

    },[first])
    
    const handleFirst = (event)=>{
        console.log(event.target.value);
        setFirst(event.target.value)
    }

    const handleSecond = (event)=>{
        console.log(event.target.value);
        setSecond(event.target.value)
    }
    
    const handleInput = (event)=>{
        setValInput(event.target.value)
    }

    const addImg = async (first, valInput, second)=>{
        

        const API_KEY = 'tuHgbvhPQKq9fXJndzahTGMXbjUImNCYgqickaba'
        console.log(first)
        console.log(second)
        console.log(valInput)
        const response = await fetch(`https://api.nasa.gov/mars-photos/api/v1/rovers/${first}/photos?sol=${valInput}&camera=${second}&api_key=${API_KEY}`)

        const data = await response.json()
        console.log(data)
        // const imgMars = (data => <img src={data.photos.img_src[0]} alt = "img">{data.photos.img_src[0]} </img>)
        const imgMars = data.photos.map(foto =><div key ={data}>
                    <img src = {foto.img_src} alt="foto" />
                    </div>)
        // for (let i =0; i<data.length; i++){
        //     let imgMarsOne = data.photos[i].img_src
        //     setResult(imgMarsOne)

        // }
            
        setResult(imgMars)
    }



    return(
        <div className="paper">
            <div className="box-in-paper">
                <div className="text-about-mars">
                    <p>Марс — четвёртая по удалённости от Солнца и седьмая по размеру планета Солнечной системы; масса планеты составляет 10,7 % массы Земли. Названа в честь Марса — древнеримского бога войны, соответствующего древнегреческому Аресу.«В античной мифологии» Также Марс называют «красной планетой» из-за красноватого оттенка поверхности, придаваемого ей минералом маггемитом — γ-оксидом железа(III).«Грунт»
                        Марс — планета земной группы с разреженной атмосферой (давление на поверхности в 160 раз меньше земного). «Атмосфера и климат» Особенностями поверхностного рельефа Марса можно считать ударные кратеры наподобие лунных, а также вулканы, долины, пустыни и полярные ледниковые шапки наподобие земных.«Лёд и полярные шапки»
                        У Марса есть два естественных спутника — Фобос и Деймос (в переводе с древнегреческого — «страх» и «ужас», имена двух сыновей Ареса, сопровождавших его в бою), которые относительно малы (Фобос — 26,8×22,4×18,4 км, Деймос — 15×12,2×10,4 км)[9][10] и имеют неправильную форму.
                    </p>
                </div>
                <div className="">
                    <select value = {first} onChange={handleFirst} className="selectAll">
                        {selectName}
                    </select>
                    <select value = {second} onChange={handleSecond} className="selectAll">
                        {selectCameras}

                    </select>
                    <input onChange = {handleInput} value={valInput} placeholder="Введи время в секундах" className="selectAll"></input>
                    <button onClick={() => addImg(first, valInput, second)} className="btn">Show Foto</button>

                </div>
                
                
                <div>
                    {result}
                </div>
            </div>
          
        </div>
    )



}

export default MarsCamera;
