import React, {useState,useEffect} from "react";


const Artist = ()=>{
    
    const [inputSearch, setInputSearch] = useState('');
    const [result, setResult] = useState([]);
    
    
    
   
    const getArtist = async()=>{
        const options = {
            method: 'GET',
            headers: {
                'X-RapidAPI-Key': 'f1d641037bmsh805777c136ad783p142fecjsn73217d65f347',
                'X-RapidAPI-Host': 'genius.p.rapidapi.com'
            }
        };
        
        const response = await fetch(`https://genius.p.rapidapi.com/search?q=${inputSearch}`, options)
            const data = await response.json()
            console.log(data.response.hits)

            const artistSong = data.response.hits.map(song=><div key={data.response.hits}>

                <img src={song.full_title} alt="foto" />

            </div>)

            





            
            setResult(artistSong)

    }

    useEffect(()=>{
        getArtist(inputSearch )
    }, [])

    const handleChange =(event)=>{
        setInputSearch(event.target.value)
    }

    
    return(
        <div>
            
            <div className="box">
                <div>
                    <input value={inputSearch} onChange={handleChange}></input>
                </div>
                <button className="btn" onClick={getArtist}>Button</button>
                
                
                {result}


            </div>
            
        </div>
            
    )
    
   
}

export default Artist;
